import React, { useEffect } from 'react';
import './App.css'; // Import your CSS file for styling

const VideoParallax = () => {
  useEffect(() => {
    const handleScroll = () => {
      // Get the scroll position
      const scrollTop = window.scrollY;

      // Adjust the position of each container div based on the scroll position
      const parallaxContainers = document.querySelectorAll('.parallax-container');
      parallaxContainers.forEach((container, index) => {
        const speed = index + 1; // Adjust speed for each layer
        const translateY = -scrollTop * speed * 0.2; // Adjust scroll speed
        container.style.transform = `translateY(${translateY}px)`;
      });
    };

    // Add scroll event listener
    window.addEventListener('scroll', handleScroll);

    // Clean up function
    return () => {
      // Remove scroll event listener
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Run only once on component mount

  return (
    <div className="parallax-wrapper">
      {/* Container divs for each video layer */}
      <div className="parallax-container" style={{ zIndex: 3, width: "100%",  height: "100vh", cover }}>
        <video className="parallax-video" autoPlay loop muted>
          <source src="public/parallax/layer3.webm" type="video/webm" />
        </video>
      </div>
      <div className="parallax-container" style={{ zIndex: 2, width: "100%",  height: "100vh" }}>
        <video className="parallax-video" autoPlay loop muted>
          <source src="public/parallax/layer2.webm" type="video/webm" />
        </video>
      </div>
      <div className="parallax-container" style={{ zIndex: 1 , width: "100%",  height: "100vh"}}>
        <video className="parallax-video" autoPlay loop muted>
          <source src="public/parallax/final-impact_hottom.webm" type="video/webm" />
        </video>
      </div>
    </div>
  );
};

export default VideoParallax;
