import React, { useState } from 'react';
import './App.css'; // Import your CSS file for styling

function App() {
  const [firstPartEnded, setFirstPartEnded] = useState(false);
  const [roomCode, setRoomCode] = useState('');
  const [showRulesPopup, setShowRulesPopup] = useState(false); // State to control the visibility of the rules popup


  const handleFirstPartEnded = () => {
    setFirstPartEnded(true); // Set firstPartEnded to true when the first part of the video ends
  };

  const handleInputChange = (event) => {
    setRoomCode(event.target.value);
  };

  const handleKeyDown = () => {
    const content = document.querySelector('input');
    if (content) {
      content.classList.add('bobble');
      setTimeout(() => {
        content.classList.remove('bobble');
      }, 500); // Remove bobble class after 500 milliseconds
    }
  };

  const handleSubmit = () => {
    if (roomCode.trim() !== '') {
      // Redirect to the URL with the room code
      window.location.href = `https://limericksmash.com/games/${roomCode}`;
    }
  };

  console.log('Rendering with firstPartEnded:', firstPartEnded);

  return (
    <div className="App">
      <div className={`content ${firstPartEnded ? 'content-visible' : 'content-hidden'}`}>
      {/* Button for rules */}
          <button className="rules-button" onClick={() => setShowRulesPopup(true)}>
                <img src="/lightbulb-icon.svg" alt="Lightbulb Icon" />
              </button>
      </div>
      <div className="fullscreen-video-container">
        {! firstPartEnded ? (

          <video autoPlay muted onEnded={handleFirstPartEnded}>

          <source src="/limericksmash4.mp4" type="video/mp4" />
          Your browser does not support the video tag.
          </video>

        ) : (
          <video autoPlay loop >

            <source src="/limericksmash5.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        )}


      </div>
      <div className={`content ${firstPartEnded ? 'content-visible' : 'content-hidden'}`}>
        <h1>LIMERICK SMASH</h1>

        <div className="input-container">
          <input
              type="text"
              placeholder="Enter room code"
              value={roomCode}
              onChange={handleInputChange}
              onKeyDown={handleKeyDown}
            />
          <button onClick={handleSubmit}>Create or Join Room</button>
        </div>
        {/* Button for rules */}

      </div>
       {/* Rules popup */}
       {showRulesPopup && (
        <div className="rules-popup">
          <div className="rules-content">
            {/* Rules content goes here */}
            <h2>Rules of the Game</h2>
            <p>Here are the rules...</p>
            <button onClick={() => setShowRulesPopup(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
