import React, { useRef, useEffect } from 'react';
import { gsap } from 'gsap'; // Import GSAP

const VideoParallax = () => {
  const videoParallaxRef = useRef(null);

  useEffect(() => {
    // Function to handle scroll event
    const handleScroll = () => {
      // Calculate the scroll percentage
      const scrollPercentage = window.scrollY / (document.documentElement.scrollHeight - window.innerHeight);

      // Use GSAP to animate the video parallax
      gsap.to(videoParallaxRef.current, {
        y: `${-50 * scrollPercentage}%`, // Adjust the parallax effect here
        ease: 'none',
      });
    };

    // Add scroll event listener
    window.addEventListener('scroll', handleScroll);

    // Clean up function
    return () => {
      // Remove scroll event listener
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Run only once on component mount

  return (
    <div
      className="video-parallax"
      style={{
        position: 'relative',
        width: '100%',
        height: '100vh',
        overflow: 'hidden',
      }}
    >
      {/* Video layers with absolute positioning */}
      <video
        ref={videoParallaxRef}
        className="video-layer"
        src="parallax/layer3.mov"
        autoPlay
        muted
        style={{
          position: 'absolute',
          width: '100%',
          height: '100%',
          objectFit: 'cover',
          top: 0,
          left: 0,
        }}
      />
      <video
        className="video-layer"
        src="parallax/layer2.mov"
        autoPlay
        muted
        style={{
          position: 'absolute',
          width: '100%',
          height: '100%',
          objectFit: 'cover',
          top: 0,
          left: 0,
        }}
      />
      <video
        className="video-layer"
        src="parallax/Final Impact_hottom.mov"
        autoPlay
        muted
        style={{
          position: 'absolute',
          width: '100%',
          height: '100%',
          objectFit: 'cover',
          top: 0,
          left: 0,
        }}
      />
    </div>
  );
};

export default VideoParallax;
