  return (
    <div className="parallax-wrapper">
      {/* Container divs for each video layer */}
      <div className="parallax-container" style={{ zIndex: 3, width: "100%",  height: "100vh" }}>
        <video className="parallax-video" autoPlay loop muted>
          <source src="public/parallax/layer3.webm" type="video/webm" />
        </video>
      </div>
      <div className="parallax-container" style={{ zIndex: 2, width: "100%",  height: "100vh" }}>
        <video className="parallax-video" autoPlay loop muted>
          <source src="public/parallax/layer2.webm" type="video/webm" />
        </video>
      </div>
      <div className="parallax-container" style={{ zIndex: 1 , width: "100%",  height: "100vh"}}>
        <video className="parallax-video" autoPlay loop muted>
          <source src="public/parallax/final-impact_hottom.webm" type="video/webm" />
        </video>
      </div>
    </div>
  );
};

export default VideoParallax;
