import React, { useState } from 'react';
import './App.css'; // Import your CSS file for styling

function App() {
  const [firstPartEnded, setFirstPartEnded] = useState(false);
  const [roomCode, setRoomCode] = useState('');
  const [isBobbling, setIsBobbling] = useState(false); // State to control the bobble effect
  const [keyStrokeOccurred, setKeyStrokeOccurred] = useState(false); // State to track keystroke events

  const handleFirstPartEnded = () => {
    setFirstPartEnded(true); // Set firstPartEnded to true when the first part of the video ends
    console.log('First part ended:', firstPartEnded);
  };

  const handleInputChange = (event) => {
    setRoomCode(event.target.value);
    if (!keyStrokeOccurred) {
      setIsBobbling(true); // Start bobble effect only if no keystroke event has occurred since the last bobble
      setKeyStrokeOccurred(true); // Set the flag to true for the current keystroke
    }
  };

  const handleKeyDown = () => {
    if (!isBobbling) {
      setIsBobbling(true); // Start bobble effect only if it's not already active
    }
  };

  const handleSubmit = () => {
    if (roomCode.trim() !== '') {
      // Redirect to the URL with the room code
      window.location.href = `https://limericksmash.com/games/${roomCode}`;
    }
  };

  const handleInputBlur = () => {
    setKeyStrokeOccurred(false); // Reset the flag when the input loses focus
  };

  return (
    <div className="App">
      <div className="fullscreen-video-container">
        {! firstPartEnded ? (

          <video autoPlay muted onEnded={handleFirstPartEnded}>


          <source src="/limericksmash4.mp4" type="video/mp4" />
          Your browser does not support the video tag.
          </video>

        ) : (
          <video autoPlay loop >

            <source src="/limericksmash5.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        )}

        {/* <video autoPlay loop={firstPartEnded} muted onEnded={handleFirstPartEnded}>

          <source src={firstPartEnded ? "/limericksmash4.mp4": "/limericksmash5.mp4"} type="video/mp4" />
          Your browser does not support the video tag.
        </video> */}
      </div>
      <div className={`content ${firstPartEnded ? 'content-visible' : 'content-hidden'}`}>
        <h1>LIMERICK SMASH</h1>

        <div className="input-container">
          <input
            type="text"
            placeholder="Enter room code"
            value={roomCode}
            onChange={handleInputChange}
            onKeyDown={handleKeyDown} // Trigger bobble effect on key down event
            className={isBobbling ? 'bobble' : ''} // Apply bobble class if isBobbling is true
          />
          <button onClick={handleSubmit}>Create or Join Room</button>
        </div>
      </div>
    </div>
  );
}

export default App;
