import React, { useState } from 'react';
import './App.css'; // Import your CSS file for styling

function App() {
  const [videoEnded, setVideoEnded] = useState(false);
  const [roomCode, setRoomCode] = useState('');

  const handleVideoEnded = () => {
    setVideoEnded(true); // Set videoEnded to true when the first part of the video ends
  };

  const handleInputChange = (event) => {
    setRoomCode(event.target.value);
  };

  const handleSubmit = () => {
    if (roomCode.trim() !== '') {
      // Redirect to the URL with the room code
      window.location.href = `https://limericksmash.com/${roomCode}`;
    }
  };

  return (
    <div className="App">
      <div className="fullscreen-video-container">
        <video autoPlay muted onEnded={handleVideoEnded}>
          {/* First part of the video */}
          <source src="/limericksmash5.mp4" type="video/mp4" />
          {/* Second part of the video (looping animation) */}
          <source src="/limericksmash5part2.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>
      <div className={`content ${videoEnded ? 'content-loaded' : ''}`}>
        <h1>LIMERICK SMASH</h1>
        <div className="input-container">
          <input
            type="text"
            placeholder="Enter room code"
            value={roomCode}
            onChange={handleInputChange}
          />
          <button onClick={handleSubmit}>Create or Join Room</button>
        </div>
      </div>
    </div>
  );
}

export default App;
