import React, { useState } from 'react';
import ScrollyVideo from 'scrolly-video/dist/ScrollyVideo.cjs.jsx';
import VideoParallax from './VideoParallax';

function App() {
  const [showContent, setShowContent] = useState(false);

  const handleAnimationButtonClick = () => {
    // Trigger animation here
    setShowContent(true);
  };

  return (
    <div className="App">
      <video controls autoPlay loop muted>
          <source src="public/parallax/botto.mp4"  />
        </video>
      {/* Stack of videos with parallax effect */}


      {/* Button for triggering animation */}
      <button className="animation-button" style={{position: 'absolute', top:"50%", left:"50%", zIndex: 4}} onClick={handleAnimationButtonClick}>
        Play Animation
      </button>




      {/* Content dialogue */}
      {showContent && (
        <div className="content-dialogue">
          {/* Your content dialogue components */}
        </div>
      )}
    </div>
  );
}

export default App;
