import React, { useState, useRef, useEffect } from 'react';
import './App.css'; // Make sure to import your CSS for styling

function App() {
  const [firstPartEnded, setFirstPartEnded] = useState(false);
  const [playbackRate, setPlaybackRate] = useState(1); // Start with normal speed
  const bouncingVideoRef = useRef(null);

  const [roomCode, setRoomCode] = useState('');

  const handleFirstPartEnded = () => {
    setFirstPartEnded(true); // Set firstPartEnded to true when the first part of the video ends
    console.log(firstPartEnded)
    // Directly play the second video
    console.log('bvfc' , bouncingVideoRef.current)
    if (bouncingVideoRef.current) {
      console.log()
      bouncingVideoRef.current.play().catch(error => console.error("Video play failed", error));
      randomizePlaybackRate();
    }
  };

  // Function to randomize playback rate
  const randomizePlaybackRate = () => {
    // Random rate between 0.5 and 2, for example
    const rate = Math.random() * 1.5 + 0.5;
    setPlaybackRate(rate);
  };

  useEffect(() => {
    const video = bouncingVideoRef.current;
    if (video) {
      video.playbackRate = playbackRate;

      // Optional: Change rate on each loop
      video.addEventListener('ended', () => {
        video.play(); // Ensure it loops
        randomizePlaybackRate(); // Randomize rate for next loop
      });
    }

    // Cleanup to remove event listener
    return () => {
      if (video) {
        video.removeEventListener('ended', () => console.log("Cleanup: Removing 'ended' event listener"));
      }
    };
  }, [playbackRate, bouncingVideoRef.current]); // Re-apply when playbackRate changes or when bouncingVideoRef.current changes

  const handleInputChange = (event) => {
    setRoomCode(event.target.value);
  };

  const handleSubmit = () => {
    if (roomCode.trim() !== '') {
      window.location.href = `https://limericksmash.com/games/${roomCode}`;
    }
  };

  return (
    <div className="App">
      <div className="fullscreen-video-container">
        <video autoPlay muted onEnded={handleFirstPartEnded}>
          <source src="/limericksmash5.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        {firstPartEnded && (
        <video
          ref={bouncingVideoRef}
          autoPlay
          loop
          muted // Consider muting to ensure autoplay in most browsers
          className="looping-video"
        >
          <source src="/bouncable.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      )}
      </div>
      <div className={`content ${firstPartEnded ? 'content-visible' : 'content-hidden'}`}>
        <h1>LIMERICK SMASH</h1>
        <div className="input-container">
          <input
            type="text"
            placeholder="Enter room code"
            value={roomCode}
            onChange={handleInputChange}
          />
          <button onClick={handleSubmit}>Create or Join Room</button>
        </div>
      </div>
    </div>
  );
}

export default App;
