import React, { useState, useRef, useEffect } from 'react';
import './App.css'; // Make sure your CSS is properly set up

function App() {
  const [firstPartEnded, setFirstPartEnded] = useState(false);
  const [roomCode, setRoomCode] = useState('');
  const bouncingVideoRef = useRef(null);

  const handleFirstPartEnded = () => {
    setFirstPartEnded(true);
    // Set a slow and random playback rate for the bouncing video
    if (bouncingVideoRef.current) {
      bouncingVideoRef.current.playbackRate = getRandomPlaybackRate();
    }
  };

  useEffect(() => {
    // Periodically change the playback rate to a random slow value
    const intervalId = setInterval(() => {
      if (bouncingVideoRef.current && firstPartEnded) {
        bouncingVideoRef.current.playbackRate = getRandomPlaybackRate();
      }
    }, 5000); // Change playback rate every 5 seconds

    return () => clearInterval(intervalId);
  }, [firstPartEnded]);

  const getRandomPlaybackRate = () => {
    // Generate a random playback rate between 0.5 and 1 (you can adjust this range)
    return Math.random() * 0.5 + 0.5;
  };

  const handleInputChange = (event) => {
    setRoomCode(event.target.value);
  };

  const handleSubmit = () => {
    if (roomCode.trim() !== '') {
      window.location.href = `https://limericksmash.com/games/${roomCode}`;
    }
  };

  return (
    <div className="App">
      <div className="fullscreen-video-container">
        <video autoPlay muted onEnded={handleFirstPartEnded}>
          <source src="/limericksmash5.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        {firstPartEnded && (
          <video autoPlay loop ref={bouncingVideoRef} className="looping-video">
            <source src="/bouncable.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        )}
      </div>
      <div className={`content ${firstPartEnded ? 'content-visible' : 'content-hidden'}`}>
        <h1>LIMERICK SMASH</h1>
        <div className="input-container">
          <input
            type="text"
            placeholder="Enter room code"
            value={roomCode}
            onChange={handleInputChange}
          />
          <button onClick={handleSubmit}>Create or Join Room</button>
        </div>
      </div>
    </div>
  );
}

export default App;
