import React, { useEffect } from 'react';
import ScrollMagic from 'scrollmagic';
import 'gsap';


const VideoParallax = () => {
  useEffect(() => {
    // Create a ScrollMagic controller
    var controller = new ScrollMagic.Controller();

    var tween = gsap.to(".element", { /* animation properties */ });

    var scene = new ScrollMagic.Scene({
        triggerElement: ".trigger",
        duration: 300 // or any other duration
    })
    .setTween(tween)
    .addTo(controller);


    // Create a scene
    new ScrollMagic.Scene({
      triggerElement: '.video-parallax',
      duration: '200%', // Adjust as needed
      triggerHook: 1,
    })
    .setTween('.video-parallax', { y: '-100%', ease: 'power0.none' })
    .addTo(controller);

    // Clean up function
    return () => {
      controller.destroy(); // Destroy the controller on component unmount
    };
  }, []);

  return (
    <div className="video-parallax">
      <video className="video-layer" src="parallax/layer3.mov" autoPlay muted />
      <video className="video-layer" src="parallax/layer2.mov" autoPlay muted />
      <video className="video-layer" src="parallax/Final Impact_hottom.mov" autoPlay muted />
    </div>
  );
}

export default VideoParallax;
