import React, { useState } from 'react';
import './App.css'; // Import your CSS file for styling

function App() {
  const [firstPartEnded, setFirstPartEnded] = useState(false);
  const [roomCode, setRoomCode] = useState('');
  const [isBouncing, setIsBouncing] = useState(false);
  const bouncingVideoRef = useRef(null);



  useEffect(() => {
    if (isBouncing) {
      // Start bouncing effect when isBouncing becomes true
      bouncingVideoRef.current.play();
    } else {
      // Pause bouncing effect when isBouncing becomes false
      bouncingVideoRef.current.pause();
    }
  }, [isBouncing]);

  const handleFirstPartEnded = () => {
    setFirstPartEnded(true); // Set firstPartEnded to true when the first part of the video ends
    setIsBouncing(true); // Start the bouncing effect
  };

  const handleInputChange = (event) => {
    setRoomCode(event.target.value);
  };

  const handleSubmit = () => {
    if (roomCode.trim() !== '') {
      // Redirect to the URL with the room code
      window.location.href = `https://limericksmash.com/games/${roomCode}`;
    }
  };

  return (
    <div className="App">
      <div className="fullscreen-video-container">
        <video autoPlay muted onEnded={handleFirstPartEnded}>
          {/* First part of the video */}
          <source src="/limericksmash5.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        {firstPartEnded && (
          <video ref={bouncingVideoRef} loop className="looping-video">
            {/* Second part of the video (looping animation) */}
            <source src="/limericksmash5part2.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        )}
      </div>
      <div className={`content ${firstPartEnded ? 'content-visible' : 'content-hidden'}`}>
        <h1>LIMERICK SMASH</h1>
        <div className="input-container">
          <input
            type="text"
            placeholder="Enter room code"
            value={roomCode}
            onChange={handleInputChange}
          />
          <button onClick={handleSubmit}>Create or Join Room</button>
        </div>
      </div>
    </div>
  );
}

export default App;
