import React, { useState, useRef } from 'react';
import './App.css'; // Import your CSS file for styling

function App() {
  const [showButton, setShowButton] = useState(true);
  const [showContent, setShowContent] = useState(false);
  const videoRef1 = useRef(null);
  const videoRef2 = useRef(null);
  const videoRef3 = useRef(null);
  const [roomCode, setRoomCode] = useState('');
  const [showRulesPopup, setShowRulesPopup] = useState(false); // State to control the visibility of the rules popup

  const handleAnimationButtonClick = () => {
    // Play the first second of each video
    videoRef1.current.play();
    videoRef2.current.play();
    videoRef3.current.play();
    // Hide the button
    setShowButton(false);
  };

  const handleVideoTimeUpdate = () => {
    // If any of the videos reaches 1 second, pause it
    if (
      videoRef1.current.currentTime >= 1 ||
      videoRef2.current.currentTime >= 1 ||
      videoRef3.current.currentTime >= 1
    ) {
      videoRef1.current.pause();
      videoRef2.current.pause();
      videoRef3.current.pause();
      // Once paused, show the main content
      setShowContent(true);
    }
  };

  const handleInputChange = (event) => {
    setRoomCode(event.target.value);
  };

  const handleSubmit = () => {
    if (roomCode.trim() !== '') {
      // Redirect to the URL with the room code
      window.location.href = `https://limericksmash.com/games/${roomCode}`;
    }
  };

  return (
    <div className="App">
      {/* Video components */}

      <video
        ref={videoRef1}
        src="parallax/layer4.webm"
        style={{ display: 'none' }} // Hide the video element
        onTimeUpdate={handleVideoTimeUpdate}
      />
      <video
        ref={videoRef2}
        src="parallax/layer2.webm"
        style={{ display: 'none' }} // Hide the video element
        onTimeUpdate={handleVideoTimeUpdate}
      />
      <video
        ref={videoRef3}
        src="parallax/botto.webm"
        style={{ display: 'none' }} // Hide the video element
        onTimeUpdate={handleVideoTimeUpdate}
      />

      {/* Button for triggering animation */}
      {showButton && (
        <button className="animation-button" onClick={handleAnimationButtonClick}>
          Play Animation
        </button>
      )}

      {/* Main content */}
      {showContent && (
        <div className={`centerContent ${showRulesPopup ? 'centerContent-hidden' : ''}`}>
          <h1>LIMERICK SMASH</h1>
          <div className="input-container">
            <input
              type="text"
              placeholder="Enter room code"
              value={roomCode}
              onChange={handleInputChange}
            />
            <button onClick={handleSubmit}>Create or Join Room</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
