import React, { useState } from 'react';
import './App.css'; // Import your CSS file for styling

function App() {
  const [roomCode, setRoomCode] = useState('');
  const [isBobbling, setIsBobbling] = useState(false);

  const handleInputChange = (event) => {
    setRoomCode(event.target.value);
    setIsBobbling(true); // Trigger bobble animation
  };

  const handleSubmit = () => {
    if (roomCode.trim() !== '') {
      // Redirect to the URL with the room code
      window.location.href = `https://limericksmash.com/games/${roomCode}`;
    }
  };

  // Function to stop bobble animation after it completes once
  const handleAnimationEnd = () => {
    setIsBobbling(false);
  };

  return (
    <div className="App">
      <div className="fullscreen-video-container">
         {! firstPartEnded ? (

          <video autoPlay muted onEnded={handleFirstPartEnded}>


          <source src="/limericksmash4.mp4" type="video/mp4" />
          Your browser does not support the video tag.
          </video>

        ) : (
          <video autoPlay loop >

            <source src="/limericksmash5.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        )}
      </div>
      <div className={`content ${isBobbling ? 'bobble' : ''}`}>
        <h1>LIMERICK SMASH</h1>
        <div className="input-container">
          <input
            type="text"
            placeholder="Enter room code"
            value={roomCode}
            onChange={handleInputChange}
            onAnimationEnd={handleAnimationEnd} // Handle animation end to stop bobble
          />
          <button onClick={handleSubmit}>Create or Join Room</button>
        </div>
      </div>
    </div>
  );
}

export default App;
