import React, { useEffect } from 'react';
import ScrollMagic from 'scrollmagic';
import { gsap } from 'gsap'; // Import gsap from GSAP

const VideoParallax = () => {
  useEffect(() => {
    const controller = new ScrollMagic.Controller();

    // Create a GSAP tween
    const tween = gsap.to('.video-parallax', { y: '-100%', ease: 'power0.none' });

    new ScrollMagic.Scene({
      triggerElement: '.video-parallax',
      duration: '200%', // Adjust as needed
      triggerHook: 1,
    })
    .setTween(tween) // Set GSAP tween
    .addTo(controller);

    // Clean up function
    return () => {
      controller.destroy(); // Destroy the controller on component unmount
    };
  }, []);

  return (
    <div className="video-parallax">
      {/* Your video elements */}
    </div>
  );
}

export default VideoParallax;
